    else if (clear_rst_cnt0 && !(lreq_rst_dev[1] && reset_device_cnt == 3'h3))
      reset_device_cnt <= #TCQ 3'b000;
    else if (&lreq_rst_dev)
      reset_device_cnt <= #TCQ {1'b0, reset_device_cnt[1:0]} + 2;
