      PR_rcvd_lresp             <= #TCQ |lresp && PR_link_initialized;
      lreq_detect_stg1          <= #TCQ |lreq & {2{!(PRD_idle2_selected && pp_out_of_sync_stg1)}};//
      lreq_in_stat_detect_stg1  <= #TCQ lreq_in_stat;
