      pna_detect_stg1           <= #TCQ |pna & {2{!(PRD_idle2_selected && pp_out_of_sync_stg1)}};//
      pr_detect_stg1            <= #TCQ |pr & {2{!(PRD_idle2_selected && pp_out_of_sync_stg1)}};//
