                                    (MODE_XG == 2) ? 10002 :
                                    (MODE_XG == 3) ? 10003 :
                                    (MODE_XG == 5) ? 10005 : 10006;

  // this needs to be a wire since it is gated with sim_train_en. however,
  // sim_train_en will be tied off at the top level 
