      $finish;
    end
    if (IDLE1 && (MODE_XG == 6)) begin
      $display("ERROR: Cannot use IDLE1 mode in a 6G configuration.");
      $finish;
    end
    if ((MODE_XG == 4) || (MODE_XG > 6)) begin
      $display("ERROR: Invalid Speed Setting. MODE_XG=%0d", MODE_XG);
      $finish;
    end
  end
