// $Id: //IP3/DEV/hw/srio_gen2/srio_gen2_v4_1_16/hdl/phy/oplm/srio_gen2_v4_1_16_eval_gt_pcs_clk.v#1 $
//----------------------------------------------------------------------
//
// EVAL_GT_PCS_CLK
// Description:
// This is the evaluation timer for the PHY TX which runs on the gt_pcs_clk
//
// Hierarchy:
// PHY_TOP
//    |___OPLM_TOP
//            |___OPLM_TX
//                    |___EVAL_GT_PCS_CLK <-- this module
// ---------------------------------------------------------------------
`timescale 1ps/1ps

module srio_gen2_v4_1_16_eval_gt_pcs_clk #(
  parameter TCQ     = 100,
  parameter MODE_XG = 5)  // Line rates {1/1.25, 2/2.5, 3/3.125, 5/5, 6/6.25}
(
  output reg  flag  = 0,
  input       rst,
  input       clk
);

  //This clock will operate in the GT_PCS_CLK domain. Based on the selected mode,
  //the timeout will be calculated to obtain a time of 8 hours.
  //MODE_XG | GT_PCS_CLK (Mhz)  | Period (us) | Cycle Count Needed      
  //   1    |    31.25          |  .032       |   900,000,000,000,000 ~ (255^6)*3  = 7.3 hrs
  //   2    |    62.5           |  .016       | 1,800,000,000,000,000 ~ (255^6)*7  = 8.5 hrs
  //   3    |    78.13          |  .0123      | 2,341,463,414,634,146 ~ (255^6)*9  = 8.4 hrs
  //   5    |    125            |  .008       | 3,600,000,000,000,000 ~ (255^6)*13 = 7.9 hrs
  //   6    |    156.25         |  .0064      | 4,500,000,000,000,000 ~ (255^6)*16 = 7.8 hrs

  // Need 5 Stages reguardless of mode (A-E)
  // Initialize all registers to 0 since no reset is attached to this module

  // Stage A
  //------------------------------
  reg   [7:0] a = 0;
  reg         a_out = 0;
  wire        a_ceo;
  wire        a_en;

  assign a_en = 1'b1;

  always @(posedge clk) begin 
    if (rst) begin
      a     <= #TCQ 0;
      a_out <= #TCQ 0;
    end else begin
      if (a_en) begin
        a   <= #TCQ a + 1;
      end
      a_out <= #TCQ a_ceo;
    end
  end

  assign a_ceo = a_en & (&a);

  // Stage B
  //------------------------------
  reg   [7:0] b = 0;
  reg         b_out = 0;
  wire        b_ceo;
  wire        b_en;

  assign b_en = a_out;

  always @(posedge clk) begin
    if (rst) begin
      b     <= #TCQ 0;
      b_out <= #TCQ 0;
    end else begin
      if (b_en) begin
        b   <= #TCQ b + 1;
      end
      b_out <= #TCQ b_ceo;
    end
  end

  assign b_ceo = b_en & (&b);


  // Stage C
  //------------------------------
  reg   [7:0] c = 0;
  reg         c_out = 0;
  wire        c_ceo;
  wire        c_en;

  assign c_en = b_out;

  always @(posedge clk) begin
    if (rst) begin
      c     <= #TCQ 0;
      c_out <= #TCQ 0;
    end else begin
      if (c_en) begin
        c   <= #TCQ c + 1;
      end
      c_out <= #TCQ c_ceo;
    end
  end

  assign c_ceo = c_en & (&c);


  // Stage D
  //------------------------------
  reg   [7:0] d = 0;
  reg         d_out = 0;
  wire        d_ceo;
  wire        d_en;

  assign d_en = c_out;

  always @(posedge clk) begin
    if (rst) begin
      d     <= #TCQ 0;
      d_out <= #TCQ 0;
    end else begin
      if (d_en) begin
        d   <= #TCQ d + 1;
      end
      d_out <= #TCQ d_ceo;
    end
  end

  assign d_ceo = d_en & (&d);


  // Stage E
  //------------------------------
  reg   [7:0] e = 0;
  reg         e_out = 0;
  wire        e_ceo;
  wire        e_en;

  assign e_en = d_out;

  always @(posedge clk) begin
    if (rst) begin
      e     <= #TCQ 0;
      e_out <= #TCQ 0;
    end else begin
      if (e_en) begin
        e   <= #TCQ e + 1;
      end
      e_out <= #TCQ e_ceo;
    end
  end

  assign e_ceo = e_en & (&e);

  // Stage F
  // -----------------------------
  //This clock will operate in the GT_PCS_CLK domain. Based on the selected mode,
  //the timeout will be calculated to obtain a time of 8 hours.
  //MODE_XG | GT_PCS_CLK (Mhz)  | Period (us) | Cycle Count Needed      
  //   1    |    31.25          |  .032       |   900,000,000,000,000 ~ (255^6)*3  = 7.3 hrs
  //   2    |    62.5           |  .016       | 1,800,000,000,000,000 ~ (255^6)*7  = 8.5 hrs
  //   3    |    78.13          |  .0123      | 2,341,463,414,634,146 ~ (255^6)*9  = 8.4 hrs
  //   5    |    125            |  .008       | 3,600,000,000,000,000 ~ (255^6)*13 = 7.9 hrs
  //   6    |    156.25         |  .0064      | 4,500,000,000,000,000 ~ (255^6)*16 = 7.8 hrs
 
  //Figure out the number to count to on the last stage based on the mode
  //calculations
  localparam LAST_STAGE_COUNT = (MODE_XG == 1) ? 3 :
                                (MODE_XG == 2) ? 7 :
                                (MODE_XG == 3) ? 9 :
                                (MODE_XG == 5) ? 13 : 16;
  reg  [4:0]  f = 0;
  wire        f_ceo;
  wire        f_en;

  assign f_en = e_out;

  always @(posedge clk) begin
    if (rst) begin
      f <= #TCQ 0;
    end else begin
      if (f_en) begin
        f <= #TCQ f + 1;
      end
    end
  end

  assign f_ceo = f_en & (f == LAST_STAGE_COUNT);

  // Generate the expire flag
  //------------------------------
  always @(posedge clk) begin
    if (rst) begin
      flag <= #TCQ 0;

    end else if (f_ceo) begin
      flag <= #TCQ 1;
    end
  end

endmodule

