//----------------------------------------------------------------------
// $Id: //IP3/DEV/hw/srio_gen2/srio_gen2_v4_1_16/hdl/buf/srio_gen2_v4_1_16_eval.v#1 $
//----------------------------------------------------------------------
`timescale 1ps/1ps
(* DowngradeIPIdentifiedWarnings = "yes" *)

module srio_gen2_v4_1_16_eval
#(parameter TCQ = 100)
(
                ena,
                x,
                pci_rst,
                pci_clk
                );


  // Declare the port directions.

  input         ena;
  output        x;
  input         pci_rst;
  input         pci_clk;


  //******************************************************************//
  // Stage A.                                                         //
  //******************************************************************//


  reg     [7:0] a = 8'h00;
  reg           a_out = 1'b0;
  wire          a_ceo;
  wire          a_en;

  assign a_en = 1'b1;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_a
    if (pci_rst) begin
      a <= #TCQ 8'h00;
      a_out <= #TCQ 1'b0;
    end else begin
      if (a_en) begin
        a <= #TCQ a + 8'h01;
      end
      a_out <= #TCQ a_ceo;
    end
  end

  assign a_ceo = a_en & (a == 8'hff);


  //******************************************************************//
  // Stage B.                                                         //
  //******************************************************************//


  reg     [7:0] b = 8'h00;
  reg           b_out = 1'b0;
  wire          b_ceo;
  wire          b_en;

  assign b_en = a_out;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_b
    if (pci_rst) begin
      b <= #TCQ 8'h00;
      b_out <= #TCQ 1'b0;
    end else begin
      if (b_en) begin
        b <= #TCQ b + 8'h01;
      end
      b_out <= #TCQ b_ceo;
    end
  end

  assign b_ceo = b_en & (b == 8'hff);


  //******************************************************************//
  // Stage C.                                                         //
  //******************************************************************//


  reg     [7:0] c = 8'h00;
  reg           c_out = 1'b0;
  wire          c_ceo;
  wire          c_en;

  assign c_en = b_out;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_c
    if (pci_rst) begin
      c <= #TCQ 8'h00;
      c_out <= #TCQ 1'b0;
    end else begin
      if (c_en) begin
        c <= #TCQ c + 8'h01;
      end
      c_out <= #TCQ c_ceo;
    end
  end

  assign c_ceo = c_en & (c == 8'hff);


  //******************************************************************//
  // Stage D.                                                         //
  //******************************************************************//


  reg     [7:0] d = 8'h00;
  reg           d_out = 1'b0;
  wire          d_ceo;
  wire          d_en;

  assign d_en = c_out;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_d
    if (pci_rst) begin
      d <= #TCQ 8'h00;
      d_out <= #TCQ 1'b0;
    end else begin
      if (d_en) begin
        d <= #TCQ d + 8'h01;
      end
      d_out <= #TCQ d_ceo;
    end
  end

  assign d_ceo = d_en & (d == 8'hff);


  //******************************************************************//
  // Stage E.                                                         //
  //******************************************************************//


  reg     [7:0] e = 8'h00;
  wire          e_ceo;
  wire          e_en;

  assign e_en = d_out;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_e
    if (pci_rst) begin
      e <= #TCQ 8'h00;
    end else begin
      if (e_en) begin
        e <= #TCQ e + 8'h01;
      end
    end
  end

  assign e_ceo = e_en & (e == 8'hff);


  //******************************************************************//
  // Generate the expire flag.                                        //
  //******************************************************************//


  reg           bug_fix = 1'b0;

  always @(posedge pci_clk or posedge pci_rst) begin : xpci_bug_fix
    if (pci_rst)
      bug_fix <= #TCQ 1'b0;
    else if (e_ceo)
      bug_fix <= #TCQ 1'b1;
  end

  assign x = bug_fix & ena;


