// $Id: //IP3/DEV/hw/srio_gen2/srio_gen2_v4_1_16/hdl/log/log_arb/srio_gen2_v4_1_16_eval_log_clk.v#1 $
//----------------------------------------------------------------------
//
// EVAL_LOG_CLK
// Description:
// This is the evaluation timer for the LOG TX which runs on the log_clk
//
// Hierarchy:
// PHY_TOP
//    |___OPLM_TOP
//            |___OPLM_TX
//                    |___EVAL_GT_CLK <-- this module
// ---------------------------------------------------------------------
`timescale 1ps/1ps
// ------------------------------------------------
// below attribute is added to supress the synthesis warnings in vivado tool 8:3332
(* DowngradeIPIdentifiedWarnings="yes" *)
// ------------------------------------------------

module srio_gen2_v4_1_16_eval_log_clk #(
  parameter TCQ     = 100)
(
  output reg  flag  = 0,
  input       rst,
  input       clk
);

  // This clock will operate in the LOG_CLK domain. This module assumes
  // LOG_CLK is running at the highest rate (312.5MHz). It could very well
  // be operating at any frequency. Assuming it runs at the highest rate it
  // guarantees the timeout will not occur before 8 hours, though could trail
  // on for quite a while. However, two other eval timers make certain that
  // full capability won't trail on.
  // 8 hrs -> 480 min -> 28,800 s -> 28,800,000,000 us
  // LOG_CLK (Mhz) | Period (us) | Cycle Count Needed      
  //     312.5     |  .0032      | 9,000,000,000,000 -> (255^5)*8 ~ 7.6

  // Need 5 Stages reguardless of mode (A-E)
  // Initialize all registers to 0 since no reset is attached to this module

  // Stage A
  //------------------------------
  reg   [7:0] a = 0;
  reg         a_out = 0;
  wire        a_ceo;
  wire        a_en;

  assign a_en = 1'b1;

  always @(posedge clk) begin 
    if (rst) begin
      a     <= #TCQ 0;
      a_out <= #TCQ 0;
    end else begin
      if (a_en) begin
        a   <= #TCQ a + 1;
      end
      a_out <= #TCQ a_ceo;
    end
  end

  assign a_ceo = a_en & (&a);

  // Stage B
  //------------------------------
  reg   [7:0] b = 0;
  reg         b_out = 0;
  wire        b_ceo;
  wire        b_en;

  assign b_en = a_out;

  always @(posedge clk) begin
    if (rst) begin
      b     <= #TCQ 0;
      b_out <= #TCQ 0;
    end else begin
      if (b_en) begin
        b   <= #TCQ b + 1;
      end
      b_out <= #TCQ b_ceo;
    end
  end

  assign b_ceo = b_en & (&b);


  // Stage C
  //------------------------------
  reg   [7:0] c = 0;
  reg         c_out = 0;
  wire        c_ceo;
  wire        c_en;

  assign c_en = b_out;

  always @(posedge clk) begin
    if (rst) begin
      c     <= #TCQ 0;
      c_out <= #TCQ 0;
    end else begin
      if (c_en) begin
        c   <= #TCQ c + 1;
      end
      c_out <= #TCQ c_ceo;
    end
  end

  assign c_ceo = c_en & (&c);


  // Stage D
  //------------------------------
  reg   [7:0] d = 0;
  reg         d_out = 0;
  wire        d_ceo;
  wire        d_en;

  assign d_en = c_out;

  always @(posedge clk) begin
    if (rst) begin
      d     <= #TCQ 0;
      d_out <= #TCQ 0;
    end else begin
      if (d_en) begin
        d   <= #TCQ d + 1;
      end
      d_out <= #TCQ d_ceo;
    end
  end

  assign d_ceo = d_en & (&d);


  // Stage E
  //------------------------------
  reg   [7:0] e = 0;
  reg         e_out = 0;
  wire        e_ceo;
  wire        e_en;

  assign e_en = d_out;

  always @(posedge clk) begin
    if (rst) begin
      e     <= #TCQ 0;
      e_out <= #TCQ 0;
    end else begin
      if (e_en) begin
        e   <= #TCQ e + 1;
      end
      e_out <= #TCQ e_ceo;
    end
  end

  assign e_ceo = e_en & (&e);

  // Stage F
  // -----------------------------
  localparam LAST_STAGE_COUNT = 7;
  reg  [3:0]  f = 0;
  wire        f_ceo;
  wire        f_en;

  assign f_en = e_out;

  always @(posedge clk) begin
    if (rst) begin
      f <= #TCQ 0;
    end else begin
      if (f_en) begin
        f <= #TCQ f + 1;
      end
    end
  end

  assign f_ceo = f_en & (f == LAST_STAGE_COUNT);

  // Generate the expire flag
  //------------------------------
  always @(posedge clk) begin
    if (rst) begin
      flag <= #TCQ 0;

    end else if (f_ceo) begin
      flag <= #TCQ 1;
    end
  end

endmodule

