          2'b01: begin
            hd_keep = lhtx_tlast_hold ? 8'b1000_0000 : 8'b1111_1111;
            hd_data = {hello_addr_hold[7:3],hello_wdptr_hold,hello_xamsbs_hold,LA_lhtx_tdata[63:8]};
            hd_user = 8'bxxxx_xxxx;
            hd_last = lhtx_tlast_hold;
            hd_we   = 8'b1111_1111;
            hd_vld  = 1'b1;
          end
