            hd_keep = lhtx_tlast_hold ? 8'b1000_0000 : 8'b1111_1111;
            hd_data = {hello_data_hold[7:0],LA_lhtx_tdata[63:8]};
            hd_user = 8'bxxxx_xxxx;
            hd_last = lhtx_tlast_hold;
            hd_we   = hd_update ? 8'b1111_1111 : 8'b0000_0000;
            hd_vld  = hd_update;
          end
        endcase
