            hd_keep = lhtx_tlast_hold ?  ((lhtx_tid_hold[7:6] == 2'b00) ? {lhtx_tkeep_hold[4:0],3'b0} : {lhtx_tkeep_hold[6:0],1'b0}) : 8'b1111_1111;
