      .TCQ                       (TCQ))
    log_cfg_axi_inst
     (.core_clk                  (log_clk),
      .cfg_clk                   (cfg_clk),
      .cfg_rst                   (cfg_rst),
      .CF_awvalid                (CF_cfgl_awvalid),
