      .CCA_sync_we               (CCA_sync_we),
      .CCA_cfg_raddr             (CCA_cfg_raddr),
      .CCA_sync_re               (CCA_sync_re),
      .CC_core_rdata             (LCR_core_rdata));
  // }}} End log_cfg_axi inst --------------

  // {{{ log_cfg_reg inst ------------------
